.app-download{position: absolute;top:291px;}
.app-download p {display: block;margin-bottom: 7px;font-size: 14px;line-height: 17px;text-align: center; color:#fff;}
.app-download p:nth-of-type(1){ margin-top: 5px;}
/*nav*/
li{ list-style-type:none; }
.nav .nav_content h1{  font-size: 20px;text-align: center;line-height: 20px;color: #fff;margin-top: 5px;margin-bottom: 20px;}
.nav {width: 100%; position: fixed;	z-index: 9999;}
.nav,#n_a_v {transition: 1s;-moz-transition: 1s;-ms-transition: 1s;-o-transition: 1s;-webkit-transition: 1s;opacity: 0;filter: alpha(opacity:0);}
.nav .nav_content {
    position: fixed;
    right: 10px;
    z-index: 99999;
    width:175px;
    height: 626px;
    bottom: 10%;
}

.pr_box {margin: 0 5px;background: #fff;}
.pr_img{ width: 100%; margin: auto; display: block;}
.pr_tt{width: 100%; text-align: center; font-size: 14px;color:#000;}
.pr_price{width: 42%; text-align: left; float: left; margin-left: 8%;}

.qr_code{ width: 50%; float: left;}

.nav ul {position: absolute;width:100%;}
.nav ul .floor_nav,.nav_content a {
    transition:all 0.3s ease;
    -moz-transition:all 0.3s ease;
    -ms-transition:all 0.3s ease;
    -webkit-transition:all 0.3s ease;
}
.nav_content a:hover { color:#004058;}
.nav ul .floor_nav {color: #fff;text-align: center;line-height: 13px;padding: 10px 0;cursor: pointer;margin: 6px 0;font-size: 13px;background: #ab464e;border: 1px solid #ab464e;}
.nav .icoc{ line-height: 0px; padding: 4px 0;}
/*.nav ul .floor_nav:first-child {margin-top: 0px;line-height: 17px;}*/
.nav ul .floor_nav_cur,.nav ul .floor_nav:hover {color:#ffed00; }
.nav .nav_content ul{}
.nav ul li.nav_top {
    text-align: center;
    color: #fff;
    /* font-weight: bold; */
    font-size: 15px;
    cursor: pointer;
    background: #69d7d4;
    width: 48px;
    height: 48px;
    line-height: 35px;
    margin: 579px auto auto auto;
    padding-top: 3px;
    border-radius: 50px;
    -ms-border-radius: 50px;
    -webkit-border-radius: 50px;
    -moz-border-radius: 50px;
}
@-webkit-keyframes rotate {
    to {
        -webkit-transform:rotate(30deg)}}
@-ms-keyframes rotate {
    to {
        -ms-transform:rotate(30deg)}}
@-moz-keyframes rotate {
    to {
        -moz-transform:rotate(30deg)}}
@-o-keyframes rotate {
    to {
        -o-transform:rotate(30deg)}}
@keyframes rotate {
    to {
        transform:rotate(30deg)}}
.qr_code { width: 97px; height: 97px; margin-bottom: 24px;}

.pr_img{border: 5px #fff solid;}
.pr_img, .pr_img img, .product_img, .product_img img {width: 100%;overflow: hidden}
.pr_img img, .product_img img,.imghover {width: 100%;transitaion: .5s;-moz-transition: .5s;-o-transition: .5s;-ms-transition: .5s;-webkit-transition: .5s}
.pr_img img:hover, .product_img img:hover,.imghover:hover {transform: scale(1.1);-moz-transform: scale(1.1);-ms-transform: scale(1.1);-o-transform: scale(1.1);-webkit-transform: scale(1.1)}
.clear {clear: both}
.nav_wrap {background: #f25d1c;height: 78px;margin-bottom: 2%;width: 100%;overflow: hidden}
.nav_wrap .w_main {max-width: 1200px;margin: 0 auto}
.nav_wrap .w_main .anchor {font-size: 22px;overflow: hidden}
.nav_wrap .w_main .anchor li {padding-right: 30px;display: inline-block;width: auto}
.nav_wrap .w_main .anchor li a {display: block;line-height: 78px;color: #fff}
.nav_wrap .w_main .anchor li a.hover {color: #fae83b}
.nav_wrap .w_main .share {float: right}
.nav_wrap.nav_fixed {position: fixed;width: 100%;top: 0;z-index: 9999}
.tab_con {
    /*	width: 98%;*/margin: 0}
.tab_con li {width: 25%;float: left;margin-bottom: 1.2%}
.tab_con li .pr_box {width: 94%;margin: 0 auto;background: #fff; /*border: solid #ccc 1px*/}

.tab_btn {width: 99%;margin: 0 auto 3%}
.tab_btn li {width: 50%;position: relative;cursor: pointer;font-size: 18px;color: #000;text-align: center}
.tab_btn li .tab_btn_con {width: 98%;background: #fff;margin: 0 auto;padding: 10px 0; font-size: 30px}
.tab_btn li .tab_btn_con s:nth-of-type(1) {display: none}
.tab_btn li .tab_btn_con em:nth-of-type(1) {display: none}
.tab_btn li .tab_btn_con i:nth-of-type(1) {display: none}
.tab_btn li.active .tab_btn_con {background: #f25d1c;color: #fff;}
.tab_btn li.active .triangle {width: 0;height: 0;border-top:10px solid #f25d1c;border-right: 10px solid transparent;border-left: 10px solid transparent;position: absolute;left: 50%;display: block}
.tab_btn li .tab_count_time {display: block!important}
.coupon_wrap {max-width: 1200px;margin: 0 auto;overflow: hidden}
.coupon_wrap .swiper-container {position: relative;  margin-top:0; background: #21171f;}
.coupon_wrap .swiper-container .swiper-scrollbar {display: none}
.coupon_wrap li {width: 300px;box-sizing: border-box;padding: 5px 18px;float: left;margin-left: 0;background: url(../image/coupon_bg01.png) no-repeat;background-size: 100% auto;}
.coupon_wrap li .tp_bg {height: 4px;border-radius: 8px 8px 0 0;
    /*	background-color: #fff*/}
.coupon_wrap li .coupon_c {height: 100%;
    /*	background: url(https://deal.tomtop.com/activity/201803/coupon_bg03.png) repeat-y;*/background-size: 100% auto;padding: 10px 20px 10px;font-size: 12px;background: #fff;}
.coupon_wrap li .coupon_c .hd {text-align: center}
.coupon_wrap li .coupon_c .hd .p1 {margin-bottom: 10px}
.coupon_wrap li .coupon_c .hd .p2 {margin-bottom: 2px;font-size: 22px}
.coupon_wrap li .coupon_c .bd {text-align: center}
.coupon_wrap li .coupon_c .bd .p1 {margin-bottom: 10px}
.coupon_wrap li .coupon_c .bd .p1 .prise {font-size: 56px;color: #ce2129;margin-right: 20px}
.coupon_wrap li .coupon_c .bd .p1 .country {font-size: 14px;display: inline-block;vertical-align: middle;height: 20px;padding-left: 30px;background: url(../image/icon_where.png) left top no-repeat;line-height: 27px}
.coupon_wrap li .coupon_c .bt {text-align: center}
.coupon_wrap li .coupon_c .bt .p1 a {width: 80%;height: 30px;line-height: 30px;background: #f7e42e;text-align: center;display: block;border-radius: 4px;color: #fff;margin: 10px auto 15px;font-size: 16px}
.coupon_wrap li .coupon_c .bt .p1 a.comingsoon {background: #f93}
.coupon_wrap li .coupon_c .bt .p1 a.codeOver {background: #afafaf}
.coupon_wrap li .coupon_c .bt .p1 a.codeStart {background: #e51d26}
.coupon_wrap li .bt_bg {height: 6px;border-radius: 0 0 10px 10px;
    /*	background-color: #fff*/}
.coupon_wrap li.style1 .coupon_c {background-size: 100% auto;color: #fff;background: #d15706;}
.coupon_wrap li.style1 .coupon_c .bd {text-align: center}
.coupon_wrap li.style1 .coupon_c .bd .p1 .prise {color: #f7e42e}
.coupon_wrap li.style1 .coupon_c .bd .p1 .country {background: url(../image/icon_where2.png) left top no-repeat}
.coupon_wrap li.style1 .coupon_c .bt {text-align: center}
.coupon_wrap li.style1 .coupon_c .bt .p1 a.codeStart {background: #f7e42e!important}
.coupon_wrap li.style1 .bt_bg, .coupon_wrap li.style1 .tp_bg {
    /*	background-color: #e51d26*/}
