.b_banner_bg {width: 100%;max-width: 1920px;margin: auto;background: url(https://deal.tomtop.com/img/20190520_APPOnly_bannerbg.jpg) top no-repeat;background-size:1920px;}
.b_banner_bg .b_banner {width: 100%;max-width: 1202px;margin: auto;position: relative}
.b_banner_bg .b_banner .banner_wrap {margin: 0 auto;}
.b_banner_bg .b_banner .banner_wrap .feiji {min-width: 280px}
.b_banner_bg .b_banner .banner_wrap img{ display: block}
.b_banner_bg .b_banner .scroll_banner {width: 50%;left: 26%;top: 56%;position: absolute;overflow: hidden;padding: 20px;border-radius: 20px;background: #85300e}
.b_banner_bg .b_banner .scroll_banner .border_radius {width: 100%;border-radius: 10px;overflow: hidden}
.b_banner_bg .b_banner .scroll_banner img {width: 100%}
.b_banner_bg .b_banner .scroll_banner .prev_btn, .b_banner_bg .b_banner .scroll_banner .swiper-button-prev {width: 40px;position: absolute;background: url(../image/left_arr.png) left center no-repeat;background-size: 100%}
.b_banner_bg .b_banner .scroll_banner .next_btn, .b_banner_bg .b_banner .scroll_banner .swiper-button-next {width: 40px;position: absolute;background: url(../image/right_arr.png) left center no-repeat;background-size: 100%}
.b_banner_bg .b_banner .swiper-pagination {width: 100%;bottom: 10px}
.b_banner_bg .b_banner .swiper-pagination .swiper-pagination-bullet {margin: 0 2px}
.b_banner_bg .activity_countDown {text-align: center;position: absolute;top: 70%;left: 36%;background-size:100%;width:327px; height:129px;}
.b_banner_bg .activity_countDown p {font-size: 24px;text-align: center;color: #4a2831;margin-bottom: 3px;margin-top: 19px;}
.b_banner_bg .activity_countDown .count {height: 100%}
