            'set': function (name, value) {
                var exp = new Date();
                exp.setTime(exp.getTime() + 365 * 24 * 60 * 60 * 1000); //3天过期
                document.cookie = name + "=" + encodeURIComponent(value) + ";domain=.tomtop.com;expires=" + exp.toGMTString() + ";path=/";
                return true;
            },
            /*
            *@名称:  del 删除cookie
            *@作者： caoxl
            *@日期： 2016-06-29
