var mySPilot = {
  loginUrl: 'https://panel.socialpilot.co/login?from=extension',
  imageUrl: '',
  
  imageCanvaUrl: '',
  documentTitleCanva:'',
  
  facebook: '',
  
  twitter: '',
  
  load: function(){
    //this.loginUrl = 'http://localhost/projects/social_pilot/login.htm';
    this.loginUrl = 'https://panel.socialpilot.co/login?from=extension';
    window.addEventListener("message", mySPilot.onPostMessage, false);
  },
  insertIframe: function(){
	 // twitter share to new tab 
	if(mySPilot.twitter != ''){

		//console.log(mySPilot.twitter);
		var object = mySPilot.twitter;
		mySPilot.twitter = '';   // reset
		
		var placement = object.placement;
		var retweeted_tweet_id = object.retweeted_tweet_id;
		var retweeted_user_display_name = object.retweeted_user_display_name;
		var retweeted_user_id = object.retweeted_user_id;
		var retweeted_user_name = object.retweeted_user_name;
		var text = object.text;				
		var retweet_comment = object.retweet_comment;
		var picture = object.picture;				
		var article = object.article;

		//console.log("placement: "+placement);
		//console.log("retweeted_tweet_id: "+retweeted_tweet_id);
		//console.log("retweeted_user_display_name: "+retweeted_user_display_name);
		//console.log("retweeted_user_id: "+retweeted_user_id);
		//console.log("retweeted_user_name: "+retweeted_user_name);
		//console.log("text: "+text);
		//console.log("picture "+picture);	
				
		//console.log("article "+article);
		if (typeof article === "undefined") {
			article	= "";
		}	
		//alert(article);
		// its image post
		if(picture){
			
			var title =text;
			var url = '';
			var image = picture; //picture URL
			
		}//it’s an article
		else if(article){
			var title =text;
			var url = article; // article URL 
			var image = '';

		}// its composer, just use text
		else if(placement == 'twitter-composer'){
			
			var title =text;
			var url = ''; 
			var image = '';			
			
		}//it’s a tweet
		else{
			if (typeof retweeted_user_name == "undefined" || typeof retweeted_tweet_id == "undefined") {
				var url	= "";
			} else {
				var url = 'https://twitter.com/'+ retweeted_user_name +'/status/'+ retweeted_tweet_id;
			}
			var title =text;
			var image = '';
			
		}
		
		//console.log("title: "+title);
		//console.log("url: "+url);
		//console.log("image: "+image);

		var encodedURL = 'https://panel.socialpilot.co/posts/twitterpost?title='+ encodeURIComponent(title) +'&url='+ encodeURIComponent(url) +'&image='+ encodeURIComponent(image) ;
		//console.log(encodedURL);
		
		var tab = window.open(encodedURL,'_blank');
	}

	else{  
		mySPilotInterface.init();
		sel("#sPilotIframe").src = mySPilot.loginUrl;
	}
  },
  onPostMessage: function(e){
    var data = e.data;
    //console.log("command: "+data);
	//console.log("command: "+data.command);
	
    switch (data.command) {
      case 'sPilotResize':{  
        sel('#sPilotContent').style.width = data.width + 'px';
				sel('#sPilotContent').style.height = data.height + 'px';
				
				if(data.position){
					sel('#sPilotContent').style.position = data.position;
				}
				if(data.top){
					sel('#sPilotContent').style.top = data.top;
				}
        if(data.isCreatePost == true){
			
			// share facebook
			if(mySPilot.facebook != ''){
				var object = mySPilot.facebook;
				//console.log(mySPilot.facebook);
				var via = object.via;
				var event = object.event;
				var text = object.text;
				var picture = object.picture;
				var album = object.album;
				var url = object.url;
				var placement = object.placement;
								
				//console.log("via: "+via);
				//console.log("text: "+text);
				//console.log("picture: "+picture);
				//console.log("url: "+url);
				//console.log("placement: "+placement);				
				//console.log("event: "+event);
				
				if(placement === "facebook-composer"){ // facebook-composer
					sel('#sPilotIframe').contentWindow.postMessage({
						"command":"setUrlTitle",
						'title': text,
						'url' : ''
					}, "*");					
						
				}
				else if(placement === "facebook-timeline-status"){ //status
					if(event){// status has event
						var index = event.indexOf("?");
						if(index != -1)	event = event.substring(0, index);

						sel('#sPilotIframe').contentWindow.postMessage({
							"command":"setUrlTitle",
							'title': text,
							'url' : event
						}, "*");		
					}else{// simple status
						sel('#sPilotIframe').contentWindow.postMessage({
							"command":"setUrlTitle",
							'title': text,
							'url' : ''
						}, "*");						
					}
				}
				else if(placement === "facebook-timeline-picture"){  //picture
					if(album){//  its album
						sel('#sPilotIframe').contentWindow.postMessage({
							"command":"setUrlTitle",
							'title': text,
							'url' : album,
							'image': picture
						}, "*");		
					}else{// single picture
						sel('#sPilotIframe').contentWindow.postMessage({
							"command":"setUrlTitle",
							'title': text,
							'url' : '',
							'image': picture
						}, "*");						
					}

				}
				else if(placement === "facebook-timeline-video"){ // video
					sel('#sPilotIframe').contentWindow.postMessage({
						"command":"setUrlTitle",
						'title': text,
						'url': url,
						'image' : ''
					}, "*");
				
				}else if(placement === "facebook-timeline-link"){ // link
					sel('#sPilotIframe').contentWindow.postMessage({
						"command":"setUrlTitle",
						'title': text,
						'url' : url,
						'image' : ''
					}, "*");					
					
				}
				
								
				
				mySPilot.facebook = ''; //reset 
			}
			// share image
			else if(mySPilot.imageUrl != ''){
				//console.log("image: "+mySPilot.imageUrl);
				sel('#sPilotIframe').contentWindow.postMessage({
					"command":"setUrlTitle",
					'title': document.title,				
					'url' : location.href,
					'image': mySPilot.imageUrl
				}, "*");
				mySPilot.imageUrl = '';
				
			}
			// share canva image
			else if(mySPilot.imageCanvaUrl != ''){
				
				// sel('#sPilotIframe').contentWindow.postMessage({
				// 	"command":"setUrlTitle",
				// 	'title': mySPilot.documentTitleCanva,				
				// 	'url' : mySPilot.url,
				// 	'image': mySPilot.imageCanvaUrl
				// }, "*");
				sel('#sPilotIframe').contentWindow.postMessage({
					"command":"setUrlTitle",
					'title': mySPilot.documentTitleCanva,				
					'url' : '',
					'image': mySPilot.imageCanvaUrl
				}, "*");
				mySPilot.imageCanvaUrl = '';
				
			}
			// website share
			else if(kango.storage.getItem('btnclicked')== 1){
				kango.storage.setItem('btnclicked', 0);	
				sel('#sPilotIframe').contentWindow.postMessage({
					"command":"setUrlTitle",
					'title': document.title,
					'url' : location.href
				}, "*");

			}
			
				
		}
        
			
		sel("#sPilotClose").style.display = 'block';
        sel("#sPilotContent").style.background = '#fff';
        break;
      }
      case 'sPilotClose':{
        mySPilotInterface.onClose();
        break;
      }
    }
  }
};
mySPilot.load();




