// ==UserScript==
// @name socialpilot-image
// @exclude *://*.facebook.com*
// @exclude *://*twitter.com*
// @exclude *://*.canva.com*
// ==/UserScript==


// skip all if image option is disabled
if ( kango.storage.getItem('socialpilot.option.image-overlays') !== 'false' )
{


// Shorthand for $( document ).ready()
$(function() {
    //console.log("ready!");
	$('body').append('<img src="'+kango.io.getResourceUrl('icons/share_image.png')+'" id="socialpilot-extension-hover-button" style="cursor:pointer;display:none;" />');
	
	$('img').mouseover(function(event) { 
		var img_height	=	$(this).height();
		var img_width	=	$(this).width();
		var position 	= 	$(this).position();	
		var offset		=	$(this).offset();
		var img_top		=	offset.top;
		var img_left	=	offset.left;
		//console.log("Image Height: " + img_height + " Width: " + img_width + " Top: " + img_top + " Left: " + img_left);
		//console.log("Holder  Top: " + (parseInt(img_top)+ parseInt(img_height) - 10) + " Left: " + (parseInt(img_left)+ parseInt(img_width) - 10));
		
		if(img_height>=300 && img_width>=300) {
			
			$("#socialpilot-extension-hover-button").show();
			$("#socialpilot-extension-hover-button").css('height', 25);
			$("#socialpilot-extension-hover-button").css('width', 100);
			$("#socialpilot-extension-hover-button").css('top', (parseInt(img_top)+10));
			$("#socialpilot-extension-hover-button").css('left', (parseInt(img_left) + parseInt(img_width) - 110));
			$("#socialpilot-extension-hover-button").css('zIndex', '99999');
			$("#socialpilot-extension-hover-button").css('position', 'absolute');
			$("#socialpilot-extension-hover-button").attr('data-src',$(this).attr("src"));
			
		}
	});
	
	$('img').mouseout(function(event) { 
		$("#socialpilot-extension-hover-button").hide();
		//$("#socialpilot-extension-hover-button").attr('data-src','');
	});

	$("#socialpilot-extension-hover-button").mouseover(function(event) { 
		$("#socialpilot-extension-hover-button").show();
	});

	$("#socialpilot-extension-hover-button").click(function(event) { 
		//alert($(this).attr('data-src'));
		mySPilot.imageUrl = $(this).attr('data-src');
		mySPilot.insertIframe();
	});
});

}


