		else  cbar_data_returned(); 
		
		// 2nd click report - after the page change
		cbar_report_click(false); 
		
		if (typeof cbar_e_input[0] !=='undefined'){
			for (ww=0; ww< cbar_e_input.length; ww++){
				cbar_em_listen (cbar_e_input[ww]);
			}
		}
		
		
